import React, { useEffect, useState } from 'react';
import ReactPaginate from 'react-paginate';
import '../styles.css';

const Hospitals = () => {
  const [hospitals, setHospitals] = useState([]);
  const [currentPage, setCurrentPage] = useState(0);
  const itemsPerPage = 5;

  useEffect(() => {
    const fetchHospitals = async () => {
      // Replace with your data fetching logic
      const data = [
        { id: 1, name: 'Holy Family', address: 'Bandra' },
        { id: 2, name: 'Shruti Nursing', address: 'Bandra' },
        // Add more hospital data
      ];
      setHospitals(data);
    };
    fetchHospitals();
  }, []);

  const handlePageClick = (event) => {
    setCurrentPage(event.selected);
  };

  const offset = currentPage * itemsPerPage;
  const currentPageItems = hospitals.slice(offset, offset + itemsPerPage);

  return (
    <div className="container">
      <h2>Hospitals in Bandra</h2>
      {currentPageItems.map(hospital => (
        <div key={hospital.id} className="hospital-card">
          <h3>{hospital.name}</h3>
          <p>{hospital.address}</p>
        </div>
      ))}
      <ReactPaginate
        previousLabel={'previous'}
        nextLabel={'next'}
        pageCount={Math.ceil(hospitals.length / itemsPerPage)}
        onPageChange={handlePageClick}
        containerClassName={'pagination'}
        activeClassName={'active'}
      />
    </div>
  );
};

export default Hospitals;
