import React, { useState, useEffect } from 'react';
import '../styles.css';

const Search = ({ hospitals, medicals, diagnostics }) => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);

  useEffect(() => {
    const combinedData = [...hospitals, ...medicals, ...diagnostics];
    const filteredResults = combinedData.filter(item =>
      item.name.toLowerCase().includes(query.toLowerCase())
    );
    setResults(filteredResults);
  }, [query, hospitals, medicals, diagnostics]);

  return (
    <div>
      <form className="form-inline ml-lg-3 mt-3 mt-lg-0">
        <input
          className="form-control mr-sm-2"
          type="search"
          placeholder="Search"
          aria-label="Search"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
      </form>
      <div className="search-results">
        {results.map(result => (
          <div key={result.id} className="result-item">
            <h5>{result.name}</h5>
            <p>{result.type}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Search;
